sys_ui_action.script:
(function(action){
	try {
		void global.DevendorEmbeddedContentSync ;
		gs.addErrorMessage("You must install the global portion of the app before we can sync content. <A href='x_dete_emb_Application_Installation.do'>About The App.</A>");
		return ;
	} catch (e){
		// Security exceptions means the global part exists!
	}
	var table = action.getGlideURI().getFileFromPath();
	var i = table.indexOf("_list");
	if (i>-1)
		table = table.substr(0,i);
	if (EventRunning('x_dete_emb.edded_content_ui_action',table)){
		gs.addInfoMessage("Sync already running.");
	} else {
		gs.addInfoMessage("Sync request sent.");
		var gr = new GlideRecordSecure('x_dete_emb_sync_content_process_state');
		gr.initialize();
		gr.setValue('event_name','x_dete_emb.edded_content_ui_action');
		gr.setValue('parm1',table);
		gr.setValue('parm2',gs.getUserName());
		gr.insert();
		gs.eventQueue('x_dete_emb.edded_content_ui_action',null,table,gs.getUserName());
	}
})(action);
